    borderColor: '#ffffff',
    borderWidth: 1,
    position: 'relative',
    top: 40
  },
  nama: {
    color: "#875749",
    position: 'relative',
    top: 20
  },
  sandi: {
    color: "#875749",
    position: 'relative',
    top: 40
  }

});

export default Daftar;
